mapper.select:
select count(1) from sys_department

  DEPT_ID = #{deptId}
  and DEPT_NAME = #{deptName}
  and DEPT_NUM = #{deptNum}
  and DEPT_ABBR = #{deptAbbr}
  and PARENT_ID = #{parentId}
  and CREATE_DATE = #{createDate}
  and UPDATE_DATE = #{updateDate}
  and `STATE` = #{state}

select

from sys_department

  DEPT_ID = #{deptId}
  and DEPT_NAME = #{deptName}
  and DEPT_NUM = #{deptNum}
  and DEPT_ABBR = #{deptAbbr}
  and PARENT_ID = #{parentId}
  and CREATE_DATE = #{createDate}
  and UPDATE_DATE = #{updateDate}
  and `STATE` = #{state}

order by CREATE_DATE desc

mapper.update:
update sys_department
DEPT_NAME = #{deptName,jdbcType=VARCHAR}, DEPT_NUM = #{deptNum,jdbcType=VARCHAR}, DEPT_ABBR = #{deptAbbr,jdbcType=VARCHAR}, PARENT_ID = #{parentId,jdbcType=VARCHAR}, CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}, UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}, `STATE` = #{state,jdbcType=VARCHAR}, HAS_BRANCH = #{hasBranch,jdbcType=VARCHAR},
where DEPT_ID = #{deptId,jdbcType=VARCHAR}
