mapper.select:
select

from sys_menu

    MENU_ID = #{menuId}
    and ZH_NAME = #{name}
    and MENU_CODE = #{menuCode}
    and PARENT_ID = #{parentId}
    and CATEGORY = #{category}
    and HREF = #{href}
    and ICON = #{menuIcon}
    and CREATE_BY = #{createBy}
    and CREATE_DATE = #{createDate}
    and UPDATE_BY = #{updateBy}
    and UPDATE_DATE = #{updateDate}
    and DELETE_FLAG = #{deleteFlag}
    and REMARK = #{remark}
    and PERMISSION = #{permission}
    and HASBRANCH = #{hasBranch}

order by CREATE_DATE desc

select
count(1)
from sys_menu

    MENU_ID = #{menuId}
    and ZH_NAME = #{name}
    and MENU_CODE = #{menuCode}
    and PARENT_ID = #{parentId}
    and CATEGORY = #{category}
    and HREF = #{href}
    and ICON = #{menuIcon}
    and CREATE_BY = #{createBy}
    and CREATE_DATE = #{createDate}
    and UPDATE_BY = #{updateBy}
    and UPDATE_DATE = #{updateDate}
    and DELETE_FLAG = #{deleteFlag}
    and REMARK = #{remark}
    and PERMISSION = #{permission}
    and HASBRANCH = #{hasBranch}

mapper.update:
update sys_menu
ZH_NAME = #{name,jdbcType=VARCHAR}, MENU_CODE = #{menuCode,jdbcType=VARCHAR}, PARENT_ID = #{parentId,jdbcType=VARCHAR}, CATEGORY = #{category,jdbcType=VARCHAR}, HREF = #{href,jdbcType=VARCHAR}, ICON = #{menuIcon,jdbcType=VARCHAR}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}, UPDATE_BY = #{updateBy,jdbcType=VARCHAR}, UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}, DELETE_FLAG = #{deleteFlag,jdbcType=VARCHAR}, REMARK = #{remark,jdbcType=VARCHAR}, PERMISSION = #{permission,jdbcType=VARCHAR}, HASBRANCH = #{hasBranch,jdbcType=VARCHAR}
where MENU_ID = #{menuId,jdbcType=VARCHAR}

update sys_menu


        ZH_NAME = #{menu.name,jdbcType=VARCHAR}
        MENU_CODE = #{menu.menuCode,jdbcType=VARCHAR}
        PARENT_ID = #{menu.parentId,jdbcType=VARCHAR}
        CATEGORY = #{menu.category,jdbcType=VARCHAR}
        HREF = #{menu.href,jdbcType=VARCHAR}
        ICON = #{menu.menuIcon,jdbcType=VARCHAR}
        CREATE_BY = #{menu.createBy,jdbcType=VARCHAR}
        CREATE_DATE = #{menu.createDate,jdbcType=TIMESTAMP}
        UPDATE_BY = #{menu.updateBy,jdbcType=VARCHAR}
        UPDATE_DATE = #{menu.updateDate,jdbcType=TIMESTAMP}
        DELETE_FLAG = #{menu.deleteFlag,jdbcType=VARCHAR}
        REMARK = #{menu.remark,jdbcType=VARCHAR}
        PERMISSION = #{menu.permission,jdbcType=VARCHAR}
        HASBRANCH = #{menu.hasBranch,jdbcType=VARCHAR}



    MENU_ID in

        #{menu.menuId}
