mapper.select:
select

from sys_role r



        r.ZH_NAME = #{name,jdbcType=VARCHAR},


        and r.EN_NAME = #{eName,jdbcType=VARCHAR},


        and r.DEPT_ID = #{department.deptId,jdbcType=VARCHAR}


        and r.USEABLE_FLAG = #{useFlag,jdbcType=VARCHAR}


        and r.CREATE_BY = #{createBy,jdbcType=VARCHAR}


        and r.CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}


        and r.UPDATE_BY = #{updateBy,jdbcType=VARCHAR}


        and r.UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}


        and r.REMARK = #{remark,jdbcType=VARCHAR}


        and r.DELETE_FLAG = #{deleteFlag,jdbcType=VARCHAR}


order by r.CREATE_DATE desc

select count(1) from sys_role r


        r.ZH_NAME = #{name,jdbcType=VARCHAR},


        and r.EN_NAME = #{eName,jdbcType=VARCHAR},


        and r.DEPT_ID = #{department.deptId,jdbcType=VARCHAR}


        and r.USEABLE_FLAG = #{useFlag,jdbcType=VARCHAR}


        and r.CREATE_BY = #{createBy,jdbcType=VARCHAR}


        and r.CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}


        and r.UPDATE_BY = #{updateBy,jdbcType=VARCHAR}


        and r.UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}


        and r.REMARK = #{remark,jdbcType=VARCHAR}


        and r.DELETE_FLAG = #{deleteFlag,jdbcType=VARCHAR}
