mapper.select:
select count(1)
from sys_user

    ACCOUNT = #{account,jdbcType=VARCHAR}
    and NICKNAME = #{nickName,jdbcType=VARCHAR}
    and USERPWD = #{userPwd,jdbcType=VARCHAR}
    and USERPHOTO = #{userPhoto,jdbcType=VARCHAR}
    and CREATETIME = #{createTime,jdbcType=TIMESTAMP}
    and UPDATETIME = #{updateTime,jdbcType=TIMESTAMP}
    and LOGINFLAG = #{loginFlag,jdbcType=VARCHAR}
    and DELETEFLAG = #{deleteFlag,jdbcType=VARCHAR}
    and DEPT_ID = #{department.deptId,jdbcType=VARCHAR}
    and ACCOUNT like #{likeAccount,jdbcType=VARCHAR} escape '/' 
    and NICKNAME like #{likeNickName,jdbcType=VARCHAR} escape '/' 
    and CREATETIME like #{likeCreateTime,jdbcType=VARCHAR} escape '/'

select

from sys_user u



    u.USER_ID = #{userId,jdbcType=VARCHAR}
    and u.ACCOUNT = #{account,jdbcType=VARCHAR}
    and u.NICKNAME = #{nickName,jdbcType=VARCHAR}
    and u.USERPWD = #{userPwd,jdbcType=VARCHAR}
    and u.USERPHOTO = #{userPhoto,jdbcType=VARCHAR}
    and u.CREATETIME = #{createTime,jdbcType=TIMESTAMP}
    and u.UPDATETIME = #{updateTime,jdbcType=TIMESTAMP}
    and u.LOGINFLAG = #{loginFlag,jdbcType=VARCHAR}
    and u.DELETEFLAG = #{deleteFlag,jdbcType=VARCHAR}
    and u.DEPT_ID = #{department.deptId,jdbcType=VARCHAR}
    and u.ACCOUNT like #{likeAccount,jdbcType=VARCHAR} escape '/' 
    and u.NICKNAME like #{likeNickName,jdbcType=VARCHAR} escape '/' 
    and u.CREATETIME like #{likeCreateTime,jdbcType=VARCHAR} escape '/' 

order by CREATETIME desc

select

from sys_user u


where u.USER_ID = #{userId,jdbcType=VARCHAR}

